        // })
        // return promise;
        //https://api.ipify.org/?format=json
        const res = await axios.get("http://ip-api.com/json/")
        return res.data;
    }
    isLocationSupported() {
        return ('geolocation' in navigator);
    }
}

export default LocationService;
